import { Plugin } from 'vuepress/core';

/**
 * Options for @vuepress/plugin-google-analytics
 */
interface GoogleAnalyticsPluginOptions {
    /**
     * Google Analytics 4 Measurement ID
     *
     * @description The Measurement ID starting with 'G-' from Google Analytics 4
     */
    id: string;
    /**
     * Enable debug mode
     *
     * @description When enabled, events will be sent to DebugView for testing
     * @default false
     */
    debug?: boolean;
}

/**
 * Google Analytics plugin for VuePress
 *
 * VuePress 的 Google Analytics 插件
 *
 * @param options - Plugin options
 * @returns VuePress plugin
 *
 * @description Integrates Google Analytics 4 tracking into VuePress applications.
 * Only active in production builds unless debug mode is enabled.
 *
 * 集成 Google Analytics 4 追踪到 VuePress 应用中。
 * 仅在生产构建时启用，除非开启调试模式。
 *
 * @example
 * ```ts
 * import { googleAnalyticsPlugin } from '@vuepress/plugin-google-analytics'
 *
 * export default {
 *   plugins: [
 *     googleAnalyticsPlugin({
 *       id: 'G-XXXXXXXXXX'
 *     })
 *   ]
 * }
 * ```
 */
declare const googleAnalyticsPlugin: (options: GoogleAnalyticsPluginOptions) => Plugin;

export { googleAnalyticsPlugin };
export type { GoogleAnalyticsPluginOptions };
